canvas.example:
&lt;canvas id="boxCanvas"&gt;This blue box does not show if the browers does not support the canvas tag.&lt;/canvas&gt;
&lt;script&gt;
  var c = document.getElementById("boxCanvas");
  var ctx = c.getContext("2d");
  ctx.fillStyle = "#0000FF";
  ctx.fillRect(0, 0, 150, 150);
&lt;/script&gt;

caption.example:
&lt;p&gt;The caption element is used within a table element to provide context.&lt;/p&gt;
&lt;table style="border: 1px solid black;"&gt;
  &lt;caption&gt;
  &lt;p&gt;This is a table.&lt;/p&gt;
  &lt;/caption&gt;
  &lt;thead&gt;
    &lt;tr&gt;
      &lt;th&gt;Column 1&lt;/th&gt;
      &lt;th&gt;Column 2&lt;/th&gt;
    &lt;/tr&gt;
  &lt;/thead&gt;
  &lt;tfoot&gt;
    &lt;tr&gt;
      &lt;td&gt;Footer 1&lt;/td&gt;
      &lt;td&gt;Footer 2&lt;/td&gt;
    &lt;/tr&gt;
  &lt;/tfoot&gt;
  &lt;tbody&gt;
    &lt;tr&gt;
      &lt;td&gt;Cell 1&lt;/td&gt;
      &lt;td&gt;Cell 2&lt;/td&gt;
    &lt;/tr&gt;
  &lt;/tbody&gt;
&lt;/table&gt;

