 &lt;option value="Orange" label="orangeOption"&gt;
 &lt;option value="Yellow" label="yellowOption"&gt;
 &lt;option value="Green" label="greenOption"&gt;
 &lt;option value="Blue" label="blueOption"&gt;
 &lt;option value="Purple" label="purpleOption"&gt;
 &lt;option value="Black" label="blackOption"&gt;
 &lt;/datalist&gt;
 &lt;input type="submit"&gt;
&lt;/form&gt;

